import{_ as c}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as r,c as l,d as e,e as a,f as s,b as n}from"./app-b0773e9a.js";const i={},h=e("strong",null,"ELK 到底是什么呢？",-1),d={href:"https://www.elastic.co/cn/what-is/elk-stack",target:"_blank",rel:"noopener noreferrer"},p=n('<h1 id="_1、基础简介" tabindex="-1"><a class="header-anchor" href="#_1、基础简介" aria-hidden="true">#</a> 1、基础简介</h1><p><code>Elasticsearch 是一个分布式文档储存中间件，它不会将信息储存为列数据行，而是储存已序列化为 JSON 文档的复杂数据结构。</code></p><blockquote><p>Elasticsearch 是一个分布式文档储存中间件，它不会将信息储存为列数据行，而是储存已序列化为 JSON 文档的复杂数据结构。</p><p>es也使用Java开发并使用Lucene作为其核心来实现所有索引和搜索的功能，但是它的目的是通过简单的RESTful API来隐藏Lucene的复杂性，从而让全文搜索变得简单。</p></blockquote><h2 id="_1、核心概念" tabindex="-1"><a class="header-anchor" href="#_1、核心概念" aria-hidden="true">#</a> 1、核心概念</h2><p>1、对比关系数据库</p><blockquote><p>Relational DB -&gt; Database -&gt; Tabls -&gt; Rows -&gt; Columns</p><p>Elasticsearch -&gt; Indices -&gt; Types -&gt; Doucuments -&gt; Fields</p></blockquote><p>2、基础概念：</p><ul><li>索引：有相似特征的文档的集合，类似于数据库</li><li>类型：一个逻辑上的分类或分区，类似于表</li><li>字段：对文档数据根据不同属性进行标识，类似于字段</li><li>映射mapping：处理数据的方式和规则，类似于创建表的过程</li><li>文档：可被索引的基础信息单元，类似于数据库中的一行数据</li></ul><h2 id="_2、使用教程" tabindex="-1"><a class="header-anchor" href="#_2、使用教程" aria-hidden="true">#</a> 2、使用教程</h2>',9),_={href:"https://learnku.com/docs/elasticsearch73/7.3",target:"_blank",rel:"noopener noreferrer"};function u(f,b){const t=o("ExternalLinkIcon");return r(),l("div",null,[e("blockquote",null,[e("p",null,[h,a(" “ELK”是三个开源项目的首字母缩写，这三个项目分别是：Elasticsearch、Logstash 和 Kibana。Elasticsearch 是一个搜索和分析引擎。Logstash 是服务器端数据处理管道，能够同时从多个来源采集数据，转换数据，然后将数据发送到诸如 Elasticsearch 等“存储库”中。Kibana 则可以让用户在 Elasticsearch 中使用图形和图表对数据进行可视化。"),e("a",d,[a("ELK"),s(t)])])]),p,e("p",null,[e("a",_,[a("中文文档"),s(t)])])])}const m=c(i,[["render",u],["__file","elasticsearch.html.vue"]]);export{m as default};
